{
  ""bindings"": [
    {
      ""role"": ""roles/cloudfunctions.viewer"",
      ""members"": [
        ""user:cloud-admin@example.com""
      ],
      ""condition"": {
        ""title"": ""Expiration Condition"",
        ""description"": ""Expires on 2025-11-03"",
        ""expression"": ""request.time < timestamp('2025-11-04T00:00:00.000Z')""
      }
